/* eslint-disable react/no-unescaped-entities */
import React, { useEffect, useState, useContext } from 'react';

import * as S from './styles';

const Home = () => {
  const [style, setStyle] = useState({});
  const [openModal, setOpenModal] = useState(false);

  const onResize = () => {
    const { innerHeight, innerWidth } = window;

    if (innerHeight * 1.92 < innerWidth * 1.08) {
      setStyle({ height: innerHeight, width: innerHeight * 1.7778 });
    } else {
      setStyle({ height: innerWidth * 0.5625, width: innerWidth });
    }
  };

  useEffect(() => {
    onResize();
  }, []);
  return (
    <S.Container>
      <S.PlayButton />
    </S.Container>
  );
};

export default Home;
